SELECT timeStr,`value`,preValue
FROM simhourdata
WHERE siteId = #{siteId}
	AND itemId = #{itemId}
	AND `time` >= #{start}
	AND `time` <= #{end}

SELECT `itemId`, `timeStr`, `itemName`, `value`, `troubleCode`
FROM `simhourdata`
WHERE siteId = #{siteId}
AND `time` >= #{start}
AND `time` <= #{end}
AND `value` IS NOT NULL
AND `itemId` IN

    #{itemId}

ORDER BY `time` ASC

SELECT `value`
FROM (
SELECT @rownumber := @rownumber + 1 AS line, `value`
FROM (
SELECT `value`
FROM simhourdata
WHERE siteId = #{siteId}
AND itemId = #{itemId}
AND `time` >= #{start}
AND `time` <= #{end}
AND `value` IS NOT NULL
ORDER BY `time` ASC
) a,
(
SELECT @rownumber := -1
) b
) temp


    WHERE line % #{space} = 0

SELECT COUNT(0)
FROM simhourdata
WHERE siteId = #{siteId}
	AND itemId = #{itemId}
	AND `time` >= #{start}
	AND `time` <= #{end}
	AND `value` IS NOT NULL
ORDER BY `time` ASC

