mapper.select:
SELECT DISTINCT DATE_FORMAT(genTime,'%Y-%m-%d %T') genTime, `concentration`, `spanvalues`, `checkdata`, `itemId`,itemName
FROM `zero_check` WHERE `type`=2  AND siteId = #{siteId} AND itemId = #{itemId} AND genTime >= #{start} AND genTime < #{end}
ORDER BY genTime DESC;

SELECT DISTINCT DATE_FORMAT(genTime,'%Y-%m-%d %T') genTime, `concentration`, `spanvalues`, `checkdata`, `itemId`,itemName
FROM `zero_check` WHERE `type`=1  AND siteId = #{siteId} AND itemId = #{itemId} AND genTime >= #{start} AND genTime < #{end}
ORDER BY genTime DESC;
